const fullText = $input.first().json.output; // On r√©cup√®re l'output de l'IA
const limit = 1900; // Limite Discord avec marge
const chunks = [];
let currentChunk = "";

// On d√©coupe par film (chaque bloc commence par **)
const blocks = fullText.split(/\n(?=\*\*)/); 

for (const block of blocks) {
  const cleanBlock = block.trim();
  if (cleanBlock === "") continue;

  // Si l'ajout du film + les sauts de ligne d√©passe la limite
  if ((currentChunk + "\n\n" + cleanBlock).length > limit) {
    if (currentChunk !== "") {
      chunks.push({ json: { output: currentChunk } });
    }
    // FIX : On commence le nouveau message avec un saut de ligne pour √©viter l'effet "coll√©"
    currentChunk = "\n" + cleanBlock; 
  } else {
    if (currentChunk === "") {
      currentChunk = cleanBlock;
    } else {
      // On force un double saut de ligne entre les films pour l'a√©ration
      currentChunk += "\n\n" + cleanBlock;
    }
  }
}

// On ajoute le dernier morceau
if (currentChunk.trim() !== "") {
  chunks.push({ json: { output: currentChunk } });
}

return chunks;

