const results = $input.first().json.results || [];
const nomDuCinema = "UGC Cin√© Cit√©"; // ‚ö†Ô∏è √Ä MODIFIER pour chaque n≈ìud (Vox, Le Cosmos, Star, etc.)
const now = new Date();

const simplifiedData = results.map(item => {
  const st = item.showtimes;
  if (!st) return null;

  let allShowtimes = [];

  // Parcourt toutes les listes de s√©ances (original, dubbed, multiple...)
  Object.keys(st).forEach(key => {
    const list = st[key];
    if (Array.isArray(list)) {
      list.forEach(s => {
        const date = new Date(s.startsAt);

        // OPTIONNEL : Retire cette ligne si tu veux voir TOUTES les s√©ances de la journ√©e
        if (date < now) return; 

        const heure = date.toLocaleTimeString('fr-FR', { hour: '2-digit', minute: '2-digit' });

        // LOGIQUE DE VERSION : 
        // On n'affiche (VOST) que si la version est "ORIGINAL"
        // Si c'est de la VF (DUBBED), on laisse vide.
        let versionLabel = "";
        if (s.diffusionVersion === "ORIGINAL") {
          versionLabel = " (VOST)";
        }

        // Ajout du tag AP pour les avant-premi√®res
        let extra = s.isPreview ? " [AP]" : "";

        allShowtimes.push({
          time: heure,
          label: `\`${heure}\`${versionLabel}${extra}`,
          rawTime: s.startsAt
        });
      });
    }
  });

  // Si apr√®s filtrage il n'y a plus de s√©ances pour ce film, on l'ignore
  if (allShowtimes.length === 0) return null;

  // Tri des s√©ances par heure
  allShowtimes.sort((a, b) => a.rawTime.localeCompare(b.rawTime));

  return {
    titre: item.movie.title.toUpperCase().replace(/["¬´¬ª]/g, '').trim(),
    duree: item.movie.runtime,
    horaires: allShowtimes.map(s => s.label).join(' ')
  };
}).filter(film => film !== null);

return [{ json: { cinema: nomDuCinema, films: simplifiedData } }];

