const allInputs = $input.all();
const moviesMap = {};

allInputs.forEach(input => {
  const cinemaName = input.json.cinema;
  const films = input.json.films || [];

  films.forEach(film => {
    // Nettoyage du titre (MAJUSCULES + retrait des guillemets parasites)
    const title = film.titre.toUpperCase().replace(/["¬´¬ª]/g, '').trim();

    if (!moviesMap[title]) {
      moviesMap[title] = {
        titre: title,
        duree: film.duree,
        seances: {} // On utilise un objet vide pour ne garder que les cin√©s avec s√©ances
      };
    }

    // On mappe le nom du cin√©ma proprement
    let key = "UGC";
    if (cinemaName.includes("Vox")) key = "Vox";
    if (cinemaName.includes("Cosmos")) key = "Le Cosmos";
    if (cinemaName.includes("Star SE")) key = "Star SE";
    else if (cinemaName.includes("Star")) key = "Star";

    moviesMap[title].seances[key] = film.horaires;
  });
});

// Tri alphab√©tique des films par titre avant l'envoi
const sortedMovies = Object.values(moviesMap).sort((a, b) => a.titre.localeCompare(b.titre));

return [{ json: { liste_films: sortedMovies } }];
